mapper.insert:
INSERT INTO TB_MEMBER(
	EMAIL
  , PWD
  , MEM_NM
  , COUNTRY_CD
  , MOBILE
  , SOCIAL_NM
  , SOCIAL_ID
  , MEM_AUTH
  , DEL_YN
) VALUES (
   #{email}
 , #{pwd}
 , #{mem_nm}
 , #{country_cd}
 , #{mobile}
 , #{social_nm}
 , #{social_id}
 , '0'
 , '0'
);

mapper.update:
 
		UPDATE TB_MEMBER 
		SET PWD = #{pwd} , UPD_DT = NOW() , UPD_ID = #{userId} 
		WHERE EMAIL = #{email} 
		AND IFNULL(DEL_YN, 'N') = 'N' AND GRADE = #{grade}

mapper.select:
SELECT @RN2:=@RN2+1 AS RNUM, TB2.* FROM( SELECT @RN:=@RN+1 AS ROWNUM, TB.* FROM( SELECT EMAIL , LAST_NM , FIRST_NM , NICK_NM , BIRTHDAY , GENDER , AFFILIATION , MOBILE_NO , PWD , GRADE , DEL_YN , CRE_DT , CRE_ID , UPD_DT , UPD_ID , DATE_FORMAT(CRE_DT, '%Y-%m-%d') AS CREDT , (SELECT COUNT(*) FROM TB_MEMBER A WHERE A.DEL_YN = 'N'
) AS totalCnt FROM TB_MEMBER WHERE DEL_YN = 'N' AND EMAIL = #{email}
ORDER BY CRE_DT ) TB, (SELECT @RN:=0) R ORDER BY ROWNUM DESC ) TB2, (SELECT @RN2:=0) R

SELECT @RN2:=@RN2+1 AS RNUM, TB2.* FROM( SELECT @RN:=@RN+1 AS ROWNUM, TB.* FROM( SELECT EMAIL , LAST_NM , FIRST_NM , NICK_NM , BIRTHDAY , GENDER , AFFILIATION , MOBILE_NO , PWD , GRADE , DEL_YN , CRE_DT , CRE_ID , UPD_DT , UPD_ID
, DATE_FORMAT(CRE_DT, '%Y-%m-%d') AS CREDT , (SELECT COUNT(*) FROM TB_MEMBER A WHERE A.DEL_YN = 'N') AS totalCnt FROM TB_MEMBER WHERE DEL_YN = 'N' AND PWD = #{pwd} AND EMAIL = #{email}
ORDER BY CRE_DT ) TB, (SELECT @RN:=0) R ORDER BY ROWNUM DESC ) TB2, (SELECT @RN2:=0) R

